public void sql()
{
    String name=a1.getText();
        String phno=(a2.getText());
        String email=a3.getText();
        String pass=p1.getText();
        String repass=p2.getText();
        String captcha=a4.getText();
try {
                Class.forName("java.sql.DriverManager");
                Connection c = (Connection) DriverManager.getConnection("jdbc:mysql://localhost:3306/seolaos", "root", "Mysql@123");
                Statement st = (Statement) c.createStatement();
                String query = "insert into acc(Name, Phone, Email, Password) values ('"+name+"', '"+phno+"', '"+email+"', '"+pass+"');";
                st.executeUpdate(query);
                JOptionPane.showMessageDialog(this, "Hurray!! \nYour account is successfully created. Log in now.");
                new Sign_in().setVisible(true);
            dispose();
            }
            catch(Exception e)
            {
                JOptionPane.showMessageDialog(null, e.getMessage());
            }
}
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        dt();
        String name=a1.getText();
        String phno=(a2.getText());
        String email=a3.getText();
        String pass=p1.getText();
        String repass=p2.getText();
        String captcha=a4.getText();
        if(!pass.equals(repass))
        {
            JOptionPane.showMessageDialog(this, "Passwords Mismatch");
            p1.setText("");
            p2.setText("");
        }
        if(co1.getSelectedItem() == "Select Code")
        {
            JOptionPane.showMessageDialog(this, "Please Select Country Code");
        }
        if(email.equals(""))
        {
            JOptionPane.showMessageDialog(this, "Please enter Email");
        }
        if (pass.equals(repass)&&
            co1.getSelectedItem()!="Select Code"
            && (captcha.equals("Gohn") || captcha.equals("Ubun") || captcha.equals("lilo")
            || captcha.equals("sfhg") || captcha.equals("kdjw") || captcha.equals("hdky")
            || captcha.equals("Ntxe") || captcha.equals("Img2") || captcha.equals("Eabs")
            || captcha.equals("Kpno")
            &&!email.equals("")))
        {
            sql();
        }
        else
        {
            JOptionPane.showMessageDialog(this, "Incorrect Captcha, Try again");
        }

    }//GEN-LAST:event_jButton1ActionPerformed

